import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Asumując, że używasz axios do wywołań API

function HomePage() {
    const [images, setImages] = useState([]);

    const uploadImage = async (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);
        try {
            const response = await axios.post('http://localhost:8000/home/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    // Dodaj nagłówki autentykacji, jeśli są wymagane
                },
            });
            fetchImages(); // Odśwież obrazy po dodaniu nowego
        } catch (error) {
            console.error('Błąd podczas przesyłania obrazu:', error);
        }
    };

    const fetchImages = async () => {
        try {
            const response = await axios.get('http://localhost:8000/home/');
            setImages(response.data);
        } catch (error) {
            console.error('Błąd podczas pobierania obrazów:', error);
        }
    };

    useEffect(() => {
        fetchImages();
    }, []);

    return (
        <div>
            <h1>HomePage</h1>
            <form onSubmit={uploadImage}>
                <input type="file" name="image" required />
                <input type="text" name="title" placeholder="Title" required />
                <textarea name="description" placeholder="Description" required />
                <button type="submit">Upload Image</button>
            </form>
            <div className="image-gallery">
                {images.map((img) => (
                    <div key={img.id}>
                        <img src={img.image} alt={img.title} />
                        <h3>{img.title}</h3>
                        <p>{img.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HomePage;
