import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


function HomePage() {
    const [images, setImages] = useState([]);
    const [filteredImages, setFilteredImages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const token = localStorage.getItem('access_token');
    const [imageTitle, setImageTitle] = useState('');
    const [imageDescription, setImageDescription] = useState('');
    const [fileName, setFileName] = useState('Nie wybrano pliku');

    const handleLogout = () => {
        localStorage.removeItem('access_token');  // Usuń token z localStorage
        // Przekieruj na stronę logowania lub inną stronę
        window.location.href = '/login';  // Zmień '/login' na URL Twojej strony logowania
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFileName(file ? file.name : 'Nie wybrano pliku');
    };

    const handleLike = async (imageId) => {
        try {
            await axios.post(`http://localhost:8000/api/like/${imageId}/`, {}, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            fetchImages();
        } catch (error) {
            console.error('Error liking image:', error);
        }
    };

    const handleDislike = async (imageId) => {
        try {
            await axios.post(`http://localhost:8000/api/dislike/${imageId}/`, {}, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            fetchImages();
        } catch (error) {
            console.error('Error disliking image:', error);
        }
    };

    const uploadImage = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        // Pobranie danych z formularza i dodanie ich do formData
        const imageFile = event.target.elements.image.files[0];
        formData.append('image', imageFile);
        formData.append('title', imageTitle);
        formData.append('description', imageDescription);

        try {
            const response = await axios.post('http://localhost:8000/home/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${token}`
                },
            });
            console.log('Obraz przesłany:', response.data);
            fetchImages(); // Odśwież obrazy po dodaniu nowego
            setImageTitle('');
            setImageDescription('');
            // Wyczyść wybrany plik
            setFileName('Nie wybrano pliku');
            // Resetuj również pole wyboru pliku formularza
            event.target.elements.image.value = null;
        } catch (error) {
            console.error('Błąd podczas przesyłania obrazu:', error.response?.data || error);
        }
    };

    const fetchImages = async () => {
        try {
            const response = await axios.get('http://localhost:8000/home/', {
                headers: {
                    'Authorization': `Bearer ${token}`
                },
            });
            setImages(response.data);
            setFilteredImages(response.data);
        } catch (error) {
            console.error('Błąd podczas pobierania obrazów:', error);
        }
    };

    useEffect(() => {
        //fetchImages().then(() => setFilteredImages(images));
        fetchImages();
    }, []);


    const handleSearch = (e) => {
        e.preventDefault();
        // Jeśli searchTerm nie jest pusty, filtruj obrazy, w przeciwnym razie ustaw wszystkie
        const filtered = searchTerm
            ? images.filter((img) =>
                img.title.toLowerCase().includes(searchTerm.toLowerCase())
            )
            : images;
        
        setFilteredImages(filtered);
        
    };
    

    return (
        <div className="home-page">
            <aside className="sidebar">
                <div className="logo-container">
                    <img src="/logo/Fashionova-logos_white.png" alt="Fashionova" className="sidebar-logo" />
                </div>
                <nav className="sidebar-nav">
                    <button onClick={handleLogout} className="logout-button">Wyloguj</button>
                </nav>
            </aside>
            <main className="main-content">
                <section className="upload-section">
                <div className="search-bar-container">
                        <form onSubmit={handleSearch}>
                            <input
                                type="text"
                                placeholder="Search clothes ..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                            />
                            <button type="submit">Search</button>
                        </form>
                    </div>
                <form onSubmit={uploadImage} className="upload-form">
                    <div className="form-group">
                        <label htmlFor="image-upload" className="file-upload">
                            Wybierz plik
                            <input type="file" id="image-upload" name="image" onChange={handleFileChange} required hidden />
                        </label>
                        <span id="file-chosen">{fileName}</span>
                    </div>
                    <div className="form-group">
                        <input type="text" name="title" placeholder="Title" value={imageTitle} onChange={(e) => setImageTitle(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <textarea name="description" placeholder="Description" value={imageDescription} onChange={(e) => setImageDescription(e.target.value)} required />
                    </div>
                    <button type="submit" className="upload-button">Upload Image</button>
                    
                </form>
                </section>
                <section className="gallery-section">
                <div className="image-gallery">
                    {filteredImages.map((img) => (
                        <div key={img.id} className="image-item">
                            <img src={img.image} alt={img.title} className="gallery-image" />

                            <h3>{img.title}</h3>
                            <p>{img.description}</p>
                            <div className="image-actions">
                            <button onClick={() => handleLike(img.id)} >Like</button>
                            <span><FontAwesomeIcon icon="fa-solid fa-thumbs-up" /> {img.likes}</span>
                            <button onClick={() => handleDislike(img.id)} >Dislike</button>
                            <span>Dislikes: {img.dislikes}</span>
                            </div>
                        </div>
                    ))}
                </div>
                </section>
            </main>
        </div>
    );
}

export default HomePage;
