import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HomePage() {
    const [images, setImages] = useState([]);
    const token = localStorage.getItem('access_token');

    const handleLogout = () => {
        localStorage.removeItem('access_token');  // Usuń token z localStorage
        // Przekieruj na stronę logowania lub inną stronę
        window.location.href = '/login';  // Zmień '/login' na URL Twojej strony logowania
    };

    const handleLike = async (imageId) => {
        try {
            await axios.post(`http://localhost:8000/api/like/${imageId}/`, {}, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            fetchImages();
        } catch (error) {
            console.error('Error liking image:', error);
        }
    };

    const handleDislike = async (imageId) => {
        try {
            await axios.post(`http://localhost:8000/api/dislike/${imageId}/`, {}, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            fetchImages();
        } catch (error) {
            console.error('Error disliking image:', error);
        }
    };

    const uploadImage = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        // Pobranie danych z formularza i dodanie ich do formData
        const imageFile = event.target.elements.image.files[0];
        formData.append('image', imageFile);
        formData.append('title', event.target.elements.title.value);
        formData.append('description', event.target.elements.description.value);

        try {
            const response = await axios.post('http://localhost:8000/home/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${token}`
                },
            });
            console.log('Obraz przesłany:', response.data);
            fetchImages(); // Odśwież obrazy po dodaniu nowego
        } catch (error) {
            console.error('Błąd podczas przesyłania obrazu:', error.response?.data || error);
        }
    };

    const fetchImages = async () => {
        try {
            const response = await axios.get('http://localhost:8000/home/', {
                headers: {
                    'Authorization': `Bearer ${token}`
                },
            });
            setImages(response.data);
        } catch (error) {
            console.error('Błąd podczas pobierania obrazów:', error);
        }
    };

    useEffect(() => {
        fetchImages();
    }, []);

    return (
        <div>
            <h1>HomePage</h1>
            <button onClick={handleLogout}>Wyloguj</button>
            <form onSubmit={uploadImage}>
                <input type="file" name="image" required />
                <input type="text" name="title" placeholder="Title" required />
                <textarea name="description" placeholder="Description" required />
                <button type="submit">Upload Image</button>
            </form>
            <div className="image-gallery">
                {images.map((img) => (
                    <div key={img.id}>
                        <img src={img.image} alt={img.title} />

                        <h3>{img.title}</h3>
                        <p>{img.description}</p>
                        <button onClick={() => handleLike(img.id)}>Like</button>
                        <button onClick={() => handleDislike(img.id)}>Dislike</button>
                        <p>Likes: {img.likes} Dislikes: {img.dislikes}</p>
                        
                    </div>
                ))}
            </div>
            



        </div>
    );
}

export default HomePage;
